mapper.select:

SELECT
	u.ACTIVITY_TAG channelLabel,
	count(1) ddzInvestDays,
	sum(IFNULL(d.INCOME_, 0)) / count(DISTINCT d.USER_ID_) ddzPerInvestAmount
FROM
	ODS_MJKF_DDZ_INV_ACCOUNTLOG_DD d,
	edw_user_basic u
WHERE
	1 = 1
AND d.USER_ID_ = u.USER_ID

	AND u.ACTIVITY_TAG IN

		#{channelName}


AND d.CREATED_TIME_ >= #{firstInvBeginDate}
AND d.CREATED_TIME_ <= #{invEndDate}
AND d.TYPE_ IN ('B', 'RP')
AND EXISTS (
		select s.user_id from (
				SELECT
					u.ACTIVITY_TAG,
					u.user_id,
					CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_TIME ELSE u.NORMAL_FIRSTINVEST_TIME END minTime,
					CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_BALANCE ELSE u.NORMAL_FIRSTINVEST_BALANCE END stMoney,
					CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_Y_MONEY ELSE u.NORMAL_FIRSTINVEST_Y_MONEY END stYearMoney
				FROM
					edw_user_basic u
				WHERE
					1 = 1
				AND u.REGISTER_TIME >= #{firstInvBeginDate}
				AND u.REGISTER_TIME <= #{firstInvEndDate}
				AND (
					(
						u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
						AND u.NORMAL_FIRSTINVEST_TIME >= #{firstInvBeginDate}
						AND u.NORMAL_FIRSTINVEST_TIME <= #{firstInvEndDate}
					)
					OR (
						u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
						AND u.DEPOSITORY_FIRSTINVEST_TIME >= #{firstInvBeginDate}
						AND u.DEPOSITORY_FIRSTINVEST_TIME <= #{firstInvEndDate}
					)
				)
		) s where 1=1
		and s.minTime >= #{firstInvBeginDate}
		and s.minTime <= #{firstInvEndDate}
		and s.user_id=d.USER_ID_
)
GROUP BY
	u.ACTIVITY_TAG


SELECT
	u.ACTIVITY_TAG channelLabel,
	sum(IFNULL(p.TEND_VOUCHE, 0)) totalUseRedMoney,
	count(DISTINCT p.user_id) useRedMoneyUserNum,
	sum(IFNULL(p.TEND_VOUCHE, 0))/count(DISTINCT p.user_id) perTotalUseRedMoney

FROM
	edw_trading_project_detail p,
	edw_user_basic u
WHERE
	1 = 1
AND u.user_id = p.USER_ID
AND p.ADDTIME >= #{firstInvBeginDate}
AND p.ADDTIME <= #{invEndDate}
and p.TENDER_ACCOUNT_STATUS in (0,1)


	AND u.ACTIVITY_TAG IN

		#{channelName}


AND EXISTS (
		select s.user_id from (
				SELECT
					u.ACTIVITY_TAG,
					u.user_id,
					CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_TIME ELSE u.NORMAL_FIRSTINVEST_TIME END minTime,
					CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_BALANCE ELSE u.NORMAL_FIRSTINVEST_BALANCE END stMoney,
					CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_Y_MONEY ELSE u.NORMAL_FIRSTINVEST_Y_MONEY END stYearMoney
				FROM
					edw_user_basic u
				WHERE
					1 = 1
				AND u.REGISTER_TIME >= #{firstInvBeginDate}
				AND u.REGISTER_TIME <= #{firstInvEndDate}
				AND (
					(
						u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
						AND u.NORMAL_FIRSTINVEST_TIME >= #{firstInvBeginDate}
						AND u.NORMAL_FIRSTINVEST_TIME <= #{firstInvEndDate}
					)
					OR (
						u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
						AND u.DEPOSITORY_FIRSTINVEST_TIME >= #{firstInvBeginDate}
						AND u.DEPOSITORY_FIRSTINVEST_TIME <= #{firstInvEndDate}
					)
				)
		) s where 1=1
		and s.minTime >= #{firstInvBeginDate}
		and s.minTime <= #{firstInvEndDate}
		AND s.user_id=p.user_id
)
GROUP BY
	u.ACTIVITY_TAG

SELECT s.channelLabel, sum(investYearAmount) investYearAmount FROM ( SELECT * FROM ( SELECT u.ACTIVITY_TAG channelLabel, sum(p.money) investYearAmount FROM ( SELECT o.ASSIGNEE_USER_ID user_id, o.PAY_AMOUNT * o.REMAINING_DAYS / 360 money FROM EDW_MERGE_CRE_PUR_ORDER o WHERE 1 = 1
and o.CREATE_TIME >= #{firstInvBeginDate}
and o.CREATE_TIME <= #{invEndDate}
AND o.ASSIGNEE_USER_ID IN ( SELECT user_id FROM edw_user_basic u WHERE 1 = 1 AND u.user_id IN ( SELECT user_id FROM ( SELECT s.user_id,s.ACTIVITY_TAG, CASE WHEN ptTime IS NULL THEN cgTime WHEN cgTime IS NULL THEN ptTime WHEN ptTime > cgTime THEN cgTime ELSE ptTime END minTime, CASE WHEN ptTime IS NULL THEN cgMoney WHEN cgTime IS NULL THEN ptMoney WHEN ptTime > cgTime THEN cgMoney ELSE ptMoney END stMoney FROM ( SELECT u.user_id,u.ACTIVITY_TAG,max(u.NORMAL_FIRSTINVEST_TIME) ptTime,max(u.DEPOSITORY_FIRSTINVEST_TIME) cgTime,max(u.NORMAL_FIRSTINVEST_BALANCE) ptMoney, max(u.DEPOSITORY_FIRSTINVEST_BALANCE) cgMoney FROM edw_user_basic u WHERE 1 = 1 AND ( ( u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
and u.NORMAL_FIRSTINVEST_TIME >= #{firstInvBeginDate}
and u.NORMAL_FIRSTINVEST_TIME <= #{invEndDate}
) OR ( u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
and u.DEPOSITORY_FIRSTINVEST_TIME >= #{firstInvBeginDate}
and u.DEPOSITORY_FIRSTINVEST_TIME <= #{invEndDate}
) ) GROUP BY u.user_id ) s ) s WHERE 1 = 1
and s.minTime >= #{firstInvBeginDate}
and s.minTime <= #{invEndDate}
) ) ) p, edw_user_basic u WHERE p.user_id = u.USER_ID GROUP BY u.ACTIVITY_TAG ) a1 UNION ALL SELECT * FROM ( SELECT u.ACTIVITY_TAG channelLabel, sum(p.money) investYearAmount FROM ( SELECT p.user_id, p.TENDER_CAPITAL * p.BORROW_PERIOD / 360 money FROM edw_trading_project_detail p WHERE 1 = 1
and p.ADDTIME >= #{firstInvBeginDate}
and p.ADDTIME <= #{invEndDate}
AND p.user_id IN ( SELECT user_id FROM edw_user_basic u WHERE 1 = 1 AND u.user_id IN ( SELECT user_id FROM ( SELECT s.user_id, s.ACTIVITY_TAG, CASE WHEN ptTime IS NULL THEN cgTime WHEN cgTime IS NULL THEN ptTime WHEN ptTime > cgTime THEN cgTime ELSE ptTime END minTime, CASE WHEN ptTime IS NULL THEN cgMoney WHEN cgTime IS NULL THEN ptMoney WHEN ptTime > cgTime THEN cgMoney ELSE ptMoney END stMoney FROM ( SELECT u.user_id,u.ACTIVITY_TAG,max(u.NORMAL_FIRSTINVEST_TIME) ptTime,max(u.DEPOSITORY_FIRSTINVEST_TIME) cgTime,max(u.NORMAL_FIRSTINVEST_BALANCE) ptMoney,max(u.DEPOSITORY_FIRSTINVEST_BALANCE) cgMoney FROM edw_user_basic u WHERE 1 = 1 AND ( ( u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
and u.NORMAL_FIRSTINVEST_TIME >= #{firstInvBeginDate}
and u.NORMAL_FIRSTINVEST_TIME <= #{invEndDate}
) OR ( u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
and u.DEPOSITORY_FIRSTINVEST_TIME >= #{firstInvBeginDate}
and u.DEPOSITORY_FIRSTINVEST_TIME <= #{invEndDate}
) ) GROUP BY u.user_id ) s ) s WHERE 1 = 1
and s.minTime >= #{firstInvBeginDate}
and s.minTime <= #{invEndDate}
) ) ) p, edw_user_basic u WHERE p.user_id = u.USER_ID GROUP BY u.ACTIVITY_TAG ) a2 ) s where 1=1
AND s.channelLabel IN
#{channelName}
GROUP BY s.channelLabel


SELECT
	u.ACTIVITY_TAG channelLabel,
	sum(IFNULL(case when p.TENDER_SUBJECT=2 then p.TEND_CASH else p.TENDER_CAPITAL end , 0)) investAmount,
	sum(
		IFNULL(case when p.TENDER_SUBJECT=2 then p.TEND_CASH else p.TENDER_CAPITAL end , 0) * IFNULL(p.BORROW_PERIOD, 0) / 360
	) investYearAmount
FROM
	(
		SELECT
			u.ACTIVITY_TAG,
			u.user_id,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_TIME ELSE u.NORMAL_FIRSTINVEST_TIME END minTime,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_BALANCE ELSE u.NORMAL_FIRSTINVEST_BALANCE END stMoney,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_Y_MONEY ELSE u.NORMAL_FIRSTINVEST_Y_MONEY END stYearMoney
		FROM
			edw_user_basic u
		WHERE
			1 = 1

			AND u.ACTIVITY_TAG IN

				#{channelName}


		AND u.REGISTER_TIME >= #{firstInvBeginDate}
		AND u.REGISTER_TIME <= #{firstInvEndDate}
		AND (
			(
				u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
				AND u.NORMAL_FIRSTINVEST_TIME >= #{firstInvBeginDate}
				AND u.NORMAL_FIRSTINVEST_TIME  <= #{firstInvEndDate}
			)
			OR (
				u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
				AND u.DEPOSITORY_FIRSTINVEST_TIME >= #{firstInvBeginDate}
				AND u.DEPOSITORY_FIRSTINVEST_TIME  <= #{firstInvEndDate}
			)
		)
	) u,
	edw_trading_project_detail p
WHERE
	1 = 1
AND u.user_id = p.USER_ID
AND u.minTime >= #{firstInvBeginDate}
AND u.minTime  <= #{firstInvEndDate}
AND p.ADDTIME >= #{firstInvBeginDate}
AND p.ADDTIME  <= #{invEndDate}
and p.TENDER_ACCOUNT_STATUS in (0,1)

GROUP BY
	u.ACTIVITY_TAG


SELECT
	s.ACTIVITY_TAG channelLabel,
	sum(s.stMoney) firstInvestAmount
FROM
	(
		SELECT
			u.ACTIVITY_TAG,
			u.user_id,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_TIME ELSE u.NORMAL_FIRSTINVEST_TIME END minTime,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_BALANCE ELSE u.NORMAL_FIRSTINVEST_BALANCE END stMoney,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_Y_MONEY ELSE u.NORMAL_FIRSTINVEST_Y_MONEY END stYearMoney
		FROM
			edw_user_basic u
		WHERE
			1 = 1

			AND u.ACTIVITY_TAG IN

				#{channelName}


		AND u.REGISTER_TIME >= #{firstInvBeginDate}
		AND u.REGISTER_TIME <= #{firstInvEndDate}
		AND (
			(
				u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
				AND u.NORMAL_FIRSTINVEST_TIME >= #{firstInvBeginDate}
				AND u.NORMAL_FIRSTINVEST_TIME <= #{firstInvEndDate}
			)
			OR (
				u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
				AND u.DEPOSITORY_FIRSTINVEST_TIME >= #{firstInvBeginDate}
				AND u.DEPOSITORY_FIRSTINVEST_TIME <= #{firstInvEndDate}
			)
		)
) s
WHERE
	1 = 1
AND s.minTime >= #{firstInvBeginDate}
AND s.minTime <= #{firstInvEndDate}
GROUP BY
	s.ACTIVITY_TAG


	SELECT
		u.ACTIVITY_TAG channelLabel,
		count(DISTINCT s.USER_ID) investUserNum
	FROM
		(
			SELECT
				p.user_id,
				count(1) investTimes
			FROM
				(
						SELECT
							u.ACTIVITY_TAG,
							u.user_id,
							CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_TIME ELSE u.NORMAL_FIRSTINVEST_TIME END minTime,
							CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_BALANCE ELSE u.NORMAL_FIRSTINVEST_BALANCE END stMoney,
							CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_Y_MONEY ELSE u.NORMAL_FIRSTINVEST_Y_MONEY END stYearMoney
						FROM
							edw_user_basic u
						WHERE
							1 = 1

							AND u.ACTIVITY_TAG IN

								#{channelName}


						AND u.REGISTER_TIME >= #{firstInvBeginDate}
						AND u.REGISTER_TIME <= #{firstInvEndDate}
						AND (
							(
								u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
								AND u.NORMAL_FIRSTINVEST_TIME >= #{firstInvBeginDate}
								AND u.NORMAL_FIRSTINVEST_TIME <= #{firstInvEndDate}
							)
							OR (
								u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
								AND u.DEPOSITORY_FIRSTINVEST_TIME >= #{firstInvBeginDate}
								AND u.DEPOSITORY_FIRSTINVEST_TIME <= #{firstInvEndDate}
							)
						)

				) u ,edw_trading_project_detail p , edw_merge_acc_current e
			WHERE
				1 = 1
			AND u.user_id = p.user_id
			and u.user_id = e.user_id

			and (e.NOR_BALANCE+e.NOR_RECOVER_CAPITAL_WAIT+e.NOR_LOCK_AMOUNT+e.NOR_DDZ_AMOUNT+
        	e.DEP_BALANCE+e.DEP_RECOVER_CAPITAL_WAIT+e.DEP_TENDER_LOCK_AMOUNT)=0
			and u.minTime >= #{firstInvBeginDate}
			and u.minTime <= #{firstInvEndDate}

			and p.TENDER_ACCOUNT_STATUS in (0,1)
			AND p.ADDTIME >= #{firstInvBeginDate}
			AND p.ADDTIME <= #{invEndDate}
			GROUP BY
				p.user_id
			HAVING

						count(1) >= #{investTime}


						count(1) = #{investTime}

		) s,
		edw_user_basic u
	WHERE
		1 = 1
	AND s.user_id = u.USER_ID
	GROUP BY
		u.ACTIVITY_TAG


select 
	s.ACTIVITY_TAG channelLabel,
	count(s.user_id) firstInvestUserNum 
from (
		SELECT
			u.ACTIVITY_TAG,
			u.user_id,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_TIME ELSE u.NORMAL_FIRSTINVEST_TIME END minTime,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_BALANCE ELSE u.NORMAL_FIRSTINVEST_BALANCE END stMoney,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_Y_MONEY ELSE u.NORMAL_FIRSTINVEST_Y_MONEY END stYearMoney
		FROM
			edw_user_basic u
		WHERE
			1 = 1

			AND u.ACTIVITY_TAG IN

				#{channelName}


		AND u.REGISTER_TIME >= #{firstInvBeginDate}
		AND u.REGISTER_TIME <= #{firstInvEndDate}
		AND (
			(
				u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
				AND u.NORMAL_FIRSTINVEST_TIME >= #{firstInvBeginDate}
				AND u.NORMAL_FIRSTINVEST_TIME <= #{firstInvEndDate}
			)
			OR (
				u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
				AND u.DEPOSITORY_FIRSTINVEST_TIME >= #{firstInvBeginDate}
				AND u.DEPOSITORY_FIRSTINVEST_TIME <= #{firstInvEndDate}
			)
		)

) s where 1=1 
and s.minTime >= #{firstInvBeginDate}
and s.minTime <= #{firstInvEndDate}
group by s.ACTIVITY_TAG

SELECT u.ACTIVITY_TAG channelLabel, count(u.user_id) registerUserNum FROM edw_user_basic u WHERE 1 = 1
and u.REGISTER_TIME >= #{firstInvBeginDate}
and u.REGISTER_TIME <= #{firstInvEndDate}
AND u.ACTIVITY_TAG IN
#{channelName}
GROUP BY u.ACTIVITY_TAG
