mapper.select:
SET @rank = 0;
SET @pa = NULL;
	select 
		u.activity_tag channelLabel,
		count(distinct s.user_id) firstInvestUserNum,
		sum(s.money) firstInvestAmount,
		sum(s.money * s.days / 360) firstInvestYearAmount
	from (
			SELECT
			IF (
				@pa = p.user_id ,@rank :=@rank + 1 ,@rank := 1
			) rownum ,
			@pa := p.user_id user_id,
			p.addtime,
			p.TENDER_CAPITAL money, 
			p.BORROW_PERIOD days
			FROM
				edw_trading_project_detail p
			ORDER BY
				p.user_id ASC,
				p.addtime,
				p.id
	) s , edw_user_basic u
	where 1=1
	and s.rownum= #{rownum}
	AND s.addTime >= #{startDate}
	AND s.addTime <= #{endDate}

		AND u.activity_tag IN

			#{channelName}


	and s.user_id=u.user_id
	group by u.activity_tag

SELECT u.ACTIVITY_TAG channelLabel, sum( p.TENDER_CAPITAL * p.BORROW_PERIOD / 360 ) yearAmount, sum(IFNULL(p.TENDER_CAPITAL, 0)) amount FROM edw_trading_project_detail p, edw_user_basic u WHERE 1 = 1 AND p.USER_ID = u.USER_ID AND u.ACTIVITY_TAG IN (SELECT a.channel_label FROM dim_channel_cost_union a WHERE 1 = 1 AND a.fee_time =#{onlineDate}) AND p.ADDTIME >= #{startDate} AND p.ADDTIME <= #{endDate}
AND u.ACTIVITY_TAG IN
#{channelName}
GROUP BY u.ACTIVITY_TAG
