mapper.select:
SELECT s.ACTIVITY_TAG channelLabel, sum(s.money) userInvestYearAmount FROM ( SELECT u.ACTIVITY_TAG, sum( IFNULL(case when p.TENDER_SUBJECT=2 then p.TEND_CASH else p.TENDER_CAPITAL end , 0) * IFNULL(p.BORROW_PERIOD, 0) / 360 ) money FROM edw_trading_project_detail p, edw_user_basic u WHERE 1 = 1 AND p.USER_ID = u.USER_ID and u.IS_BORROWER=0
and p.ADDTIME >= #{regBeginDate}
and p.ADDTIME <= #{invEndDate}
and p.TENDER_ACCOUNT_STATUS in (0,1)
and u.REGISTER_TIME >= #{regBeginDate}
and u.REGISTER_TIME <= #{regEndDate}
GROUP BY u.ACTIVITY_TAG UNION ALL SELECT u.ACTIVITY_TAG, sum(IFNULL(d.INCOME_, 0) * 1 / 360) money FROM ODS_MJKF_DDZ_INV_ACCOUNTLOG_DD d, edw_user_basic u WHERE 1 = 1 AND d.USER_ID_ = u.USER_ID and u.IS_BORROWER=0
and d.CREATED_TIME_ >= #{regBeginDate}
and d.CREATED_TIME_ <= #{invEndDate}
AND d.TYPE_ IN ('B', 'RP')
and u.REGISTER_TIME >= #{regBeginDate}
and u.REGISTER_TIME <= #{regEndDate}
GROUP BY u.ACTIVITY_TAG ) s WHERE 1 = 1 GROUP BY s.ACTIVITY_TAG

SELECT u.ACTIVITY_TAG channelLabel, count(u.user_id) registerUserNum
FROM
edw_user_basic u WHERE 1 = 1 and u.IS_BORROWER=0
and u.REGISTER_TIME >= #{regBeginDate}
and u.REGISTER_TIME <= #{regEndDate}
GROUP BY u.ACTIVITY_TAG

select s.ACTIVITY_TAG channelLabel, count(DISTINCT s.user_id) userInvestNum from ( SELECT u.ACTIVITY_TAG, u.USER_ID FROM edw_trading_project_detail p, edw_user_basic u WHERE p.USER_ID = u.USER_ID and u.IS_BORROWER=0
and p.TENDER_ACCOUNT_STATUS in (0,1)
and p.ADDTIME >= #{regBeginDate}
and p.ADDTIME <= #{invEndDate}
and u.REGISTER_TIME >= #{regBeginDate}
and u.REGISTER_TIME <= #{regEndDate}
GROUP BY u.ACTIVITY_TAG,u.USER_ID union all SELECT u.ACTIVITY_TAG, u.USER_ID FROM ods_mjkf_ddz_inv_accountlog_dd p, edw_user_basic u WHERE p.USER_ID_ = u.USER_ID and u.IS_BORROWER=0
and p.TYPE_ in ('B', 'RP')
and p.CREATED_TIME_ >= #{regBeginDate}
and p.CREATED_TIME_ <= #{invEndDate}
and u.REGISTER_TIME >= #{regBeginDate}
and u.REGISTER_TIME <= #{regEndDate}
GROUP BY u.ACTIVITY_TAG, u.USER_ID
) s where 1=1 group by s.ACTIVITY_TAG

select s.ACTIVITY_TAG channelLabel, sum(money) userInvestAmount from ( SELECT u.ACTIVITY_TAG, sum(case when p.TENDER_SUBJECT=2 then p.TEND_CASH else p.TENDER_CAPITAL end) money
FROM edw_trading_project_detail p, edw_user_basic u WHERE p.USER_ID = u.USER_ID and u.IS_BORROWER=0 and p.TENDER_ACCOUNT_STATUS in (0,1)
and p.ADDTIME >= #{regBeginDate}
and p.ADDTIME <= #{invEndDate}
and u.REGISTER_TIME >= #{regBeginDate}
and u.REGISTER_TIME <= #{regEndDate}
GROUP BY u.ACTIVITY_TAG union all SELECT u.ACTIVITY_TAG, sum(p.INCOME_) money FROM ods_mjkf_ddz_inv_accountlog_dd p, edw_user_basic u WHERE p.USER_ID_ = u.USER_ID and u.IS_BORROWER=0 and p.TYPE_ in ('B', 'RP')
and p.CREATED_TIME_ >= #{regBeginDate}
and p.CREATED_TIME_ <= #{invEndDate}
and u.REGISTER_TIME >= #{regBeginDate}
and u.REGISTER_TIME <= #{regEndDate}
GROUP BY u.ACTIVITY_TAG
) s where 1=1 group by s.ACTIVITY_TAG

select s.channelLabel,sum(s.firstInvestUserAmount) firstInvestUserAmount from (
		select 
			s.ACTIVITY_TAG channelLabel, 
			sum(p.TENDER_CAPITAL) firstInvestUserAmount 
		from (
						SELECT
							u.ACTIVITY_TAG,
							u.user_id,
							CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_TIME WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_TIME ELSE u.NORMAL_FIRSTINVEST_TIME END minTime,
							CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_BALANCE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_BALANCE ELSE u.NORMAL_FIRSTINVEST_BALANCE END stMoney,
							CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_Y_MONEY WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_Y_MONEY ELSE u.NORMAL_FIRSTINVEST_Y_MONEY END stYearMoney
						FROM
							edw_user_basic u
						WHERE
							1 = 1
						and u.IS_BORROWER=0

						AND u.REGISTER_TIME >= #{regBeginDate}
						AND u.REGISTER_TIME <=  #{regEndDate}
						AND (
							(
								u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
								AND u.NORMAL_FIRSTINVEST_TIME >= #{regBeginDate}
								AND u.NORMAL_FIRSTINVEST_TIME <= #{invEndDate}
							)
							OR (
								u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
								AND u.DEPOSITORY_FIRSTINVEST_TIME >= #{regBeginDate}
								AND u.DEPOSITORY_FIRSTINVEST_TIME  <= #{invEndDate}
							)
						)			


			) s , edw_trading_project_detail p
		where 1=1 
		and s.user_id=p.USER_ID

		      and s.minTime  >=  #{regBeginDate}  


		      and s.minTime <=  #{invEndDate}  


		      and p.ADDTIME  >=  #{regBeginDate}  


		      and p.ADDTIME <=  #{invEndDate}  


		and p.TENDER_ACCOUNT_STATUS in (0,1)
		group by s.ACTIVITY_TAG

union all

		select 
			s.ACTIVITY_TAG channelLabel, 
			sum(p.INCOME_) firstInvestUserAmount 
		from (
						SELECT
							u.ACTIVITY_TAG,
							u.user_id,
							CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_TIME WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_TIME ELSE u.NORMAL_FIRSTINVEST_TIME END minTime,
							CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_BALANCE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_BALANCE ELSE u.NORMAL_FIRSTINVEST_BALANCE END stMoney,
							CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_Y_MONEY WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_Y_MONEY ELSE u.NORMAL_FIRSTINVEST_Y_MONEY END stYearMoney
						FROM
							edw_user_basic u
						WHERE
							1 = 1
						and u.IS_BORROWER=0

						AND u.REGISTER_TIME >= #{regBeginDate}
						AND u.REGISTER_TIME <=  #{regEndDate}
						AND (
							(
								u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
								AND u.NORMAL_FIRSTINVEST_TIME >= #{regBeginDate}
								AND u.NORMAL_FIRSTINVEST_TIME <= #{invEndDate}
							)
							OR (
								u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
								AND u.DEPOSITORY_FIRSTINVEST_TIME >= #{regBeginDate}
								AND u.DEPOSITORY_FIRSTINVEST_TIME  <= #{invEndDate}
							)
						)	
				) s , ods_mjkf_ddz_inv_accountlog_dd p
		where 1=1 
		and s.user_id=p.USER_ID_

		      and s.minTime  >=  #{regBeginDate}  


		      and s.minTime <=  #{invEndDate}  


		      and p.CREATED_TIME_  >=  #{regBeginDate}  


		      and p.CREATED_TIME_ <=  #{invEndDate}  

		and p.TYPE_  in ('B', 'RP') 
		group by s.ACTIVITY_TAG

) s 
where 1=1
group by s.channelLabel


select 
	s.ACTIVITY_TAG channelLabel, 
	sum(p.TENDER_CAPITAL) firstInvestProAmount 
from (
		SELECT
			u.ACTIVITY_TAG,
			u.user_id,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_TIME WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_TIME ELSE u.NORMAL_FIRSTINVEST_TIME END minTime,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_BALANCE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_BALANCE ELSE u.NORMAL_FIRSTINVEST_BALANCE END stMoney,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_Y_MONEY WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_Y_MONEY ELSE u.NORMAL_FIRSTINVEST_Y_MONEY END stYearMoney
		FROM
			edw_user_basic u
		WHERE
			1 = 1
		and u.IS_BORROWER=0

		AND u.REGISTER_TIME >=  #{regBeginDate}
		AND u.REGISTER_TIME <=  #{regEndDate}
		AND (
			(
				u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
				AND u.NORMAL_FIRSTINVEST_TIME >=  #{regBeginDate}
				AND u.NORMAL_FIRSTINVEST_TIME <=  #{invEndDate}
			)
			OR (
				u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
				AND u.DEPOSITORY_FIRSTINVEST_TIME >=  #{regBeginDate}
				AND u.DEPOSITORY_FIRSTINVEST_TIME <=  #{invEndDate}
			)
		)
) s , edw_trading_project_detail p
where 1=1 
and s.user_id=p.USER_ID
and s.minTime >=  #{regBeginDate} 
and s.minTime <= #{invEndDate}
and p.ADDTIME >=  #{regBeginDate} 
and p.ADDTIME <= #{invEndDate}
and p.TENDER_SUBJECT=1
and p.TENDER_ACCOUNT_STATUS in (0,1)
group by s.ACTIVITY_TAG


SELECT
	u.ACTIVITY_TAG channelLabel,
	count(distinct p.user_id) multipleUser
FROM
	(
		SELECT
			u.ACTIVITY_TAG,
			u.user_id,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_TIME WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_TIME ELSE u.NORMAL_FIRSTINVEST_TIME END minTime,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_BALANCE WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_BALANCE ELSE u.NORMAL_FIRSTINVEST_BALANCE END stMoney,
			CASE WHEN u.NORMAL_FIRSTINVEST_TIME is null then u.DEPOSITORY_FIRSTINVEST_Y_MONEY WHEN u.NORMAL_FIRSTINVEST_TIME > u.DEPOSITORY_FIRSTINVEST_TIME THEN u.DEPOSITORY_FIRSTINVEST_Y_MONEY ELSE u.NORMAL_FIRSTINVEST_Y_MONEY END stYearMoney
		FROM
			edw_user_basic u
		WHERE
			1 = 1
		and u.IS_BORROWER=0
		AND u.REGISTER_TIME >=  #{regBeginDate} 
		AND u.REGISTER_TIME <=  #{regEndDate} 

		AND (
			(
				u.NORMAL_FIRSTINVEST_TIME IS NOT NULL
				AND u.NORMAL_FIRSTINVEST_TIME >=  #{regBeginDate} 
				AND u.NORMAL_FIRSTINVEST_TIME <=  #{invEndDate}
			)
			OR (
				u.DEPOSITORY_FIRSTINVEST_TIME IS NOT NULL
				AND u.DEPOSITORY_FIRSTINVEST_TIME >=  #{regBeginDate} 
				AND u.DEPOSITORY_FIRSTINVEST_TIME <=  #{invEndDate}
			)
		)

) u, edw_trading_project_detail p

WHERE
	1 = 1
AND u.user_id = p.user_id
AND u.minTime != p.ADDTIME
AND u.minTime >= #{regBeginDate} 
AND u.minTime  <=  #{invEndDate}
AND p.ADDTIME >= #{regBeginDate} 
AND p.ADDTIME <=  #{invEndDate}
and p.TENDER_ACCOUNT_STATUS in (0,1)

group by u.ACTIVITY_TAG
