mapper.select:
DROP TABLE IF EXISTS TMP; CREATE TEMPORARY TABLE TMP AS SELECT STAT_PERIOD, INDICATORS_NAME, indicators_value, CONCAT(sequential*100,'%') as sequential , CONCAT(compared*100,'%') as compared, month_mean_value, CONCAT(month_mean_value_than*100,'%') as month_mean_value_than, NUM FROM mdtxbi.DM_REPORT_DAILY WHERE 1=1
AND STAT_PERIOD BETWEEN #{reg_begindate} AND #{reg_enddate}
;
SELECT * FROM( SELECT STAT_PERIOD, INDICATORS_NAME, case when NUM in(7,8,9,10,11,12,15,16,37,38,39,40) then Format(indicators_value,'') else Format(indicators_value, 2) end AS indicators_value, sequential, compared, case when NUM in(7,8,9,10,11,12,15,16,37,38,39,40) then Format(month_mean_value,'') else Format(month_mean_value, 2) end AS month_mean_value, month_mean_value_than, NUM FROM TMP ORDER BY NUM,INDICATORS_NAME) s ORDER BY s.STAT_PERIOD DESC,s.NUM ;
