mapper.select:
DROP TABLE IF EXISTS TMPaaa; CREATE TEMPORARY TABLE TMPaaa AS select STAT_PERIOD, sales_type, period, sum(sales_money) as sum from dm_report_dep_sales WHERE 1=1 AND sales_type in ('项目','智投乐','月悦升')
AND STAT_PERIOD BETWEEN #{reg_begindate} AND #{reg_enddate}
GROUP BY STAT_PERIOD desc,sales_type desc,PERIOD ;
DROP TABLE IF EXISTS TMPbbb; CREATE TEMPORARY TABLE TMPbbb AS select STAT_PERIOD, sales_type, shiwutian, yiyue, eryue, sanyue, liuyue, bayue, jiuyue, shiyue, shieryue, shiwuyue, shibayue, ershisiyue, sanshiwuyue, sanshiliuyue, sishibayue, liushiyue, jiushiliuyue, shiwutian+yiyue+eryue+sanyue+liuyue+bayue+jiuyue+shiyue+shieryue+shiwuyue+ shibayue+ershisiyue+sanshiwuyue+sanshiliuyue+sishibayue+liushiyue+jiushiliuyue as zongji, 1 as num from ( SELECT STAT_PERIOD, sales_type, MAX(CASE period WHEN '15天' THEN sum ELSE 0 END ) shiwutian, MAX(CASE period WHEN '1月' THEN sum ELSE 0 END ) yiyue, MAX(CASE period WHEN '2月' THEN sum ELSE 0 END ) eryue, MAX(CASE period WHEN '3月' THEN sum ELSE 0 END ) sanyue, MAX(CASE period WHEN '6月' THEN sum ELSE 0 END ) liuyue, MAX(CASE period WHEN '8月' THEN sum ELSE 0 END ) bayue, MAX(CASE period WHEN '9月' THEN sum ELSE 0 END ) jiuyue, MAX(CASE period WHEN '10月' THEN sum ELSE 0 END ) shiyue, MAX(CASE period WHEN '12月' THEN sum ELSE 0 END ) shieryue, MAX(CASE period WHEN '15月' THEN sum ELSE 0 END ) shiwuyue, MAX(CASE period WHEN '18月' THEN sum ELSE 0 END ) shibayue, MAX(CASE period WHEN '24月' THEN sum ELSE 0 END ) ershisiyue, MAX(CASE period WHEN '35月' THEN sum ELSE 0 END ) sanshiwuyue, MAX(CASE period WHEN '36月' THEN sum ELSE 0 END ) sanshiliuyue, MAX(CASE period WHEN '48月' THEN sum ELSE 0 END ) sishibayue, MAX(CASE period WHEN '60月' THEN sum ELSE 0 END ) liushiyue, MAX(CASE period WHEN '96月' THEN sum ELSE 0 END ) jiushiliuyue, MAX(sum) from TMPaaa GROUP BY STAT_PERIOD,sales_type ) s ;
DROP TABLE IF EXISTS TMPccc; CREATE TEMPORARY TABLE TMPccc AS SELECT STAT_PERIOD,SUM(ZONGJI) AS SUM_ALL FROM TMPbbb GROUP BY STAT_PERIOD ;
DROP TABLE IF EXISTS TMPddd; CREATE TEMPORARY TABLE TMPddd AS SELECT b.*,SUM_ALL FROM TMPbbb b LEFT JOIN TMPccc c on b.STAT_PERIOD=c.STAT_PERIOD ;
DROP TABLE IF EXISTS TMPeee; CREATE TEMPORARY TABLE TMPeee AS select STAT_PERIOD, sales_type, shiwutian, yiyue, eryue, sanyue, liuyue, bayue, jiuyue, shiyue, shieryue, shiwuyue, shibayue, ershisiyue, sanshiwuyue, sanshiliuyue, sishibayue, liushiyue, jiushiliuyue, zongji, CONCAT(round(zongji*100/sum_all,2),'%') as zhanbi, num from TMPddd ;
DROP TABLE IF EXISTS TMPyyy; CREATE TEMPORARY TABLE TMPyyy AS select STAT_PERIOD, '总计', sum(shiwutian) as shiwutian, sum(yiyue) as yiyue, sum(eryue) as eryue , sum(sanyue) as sanyue, sum(liuyue) as liuyue, sum(bayue) as bayue, sum(jiuyue) as jiuyue, sum(shiyue) as shiyue, sum(shieryue) as shieryue, sum(shiwuyue) as shiwuyue, sum(shibayue) as shibayue, sum(ershisiyue) as ershisiyue, sum(sanshiwuyue) as sanshiwuyue, sum(sanshiliuyue) as sanshiliuyue, sum(sishibayue) as sishibayue, sum(liushiyue) as liushiyue, sum(jiushiliuyue) as jiushiliuyue, sum(zongji) as zongji, concat(sum(zhanbi)*100,'%') as zhanbi, 2 as num FROM (select STAT_PERIOD, sales_type, shiwutian, yiyue, eryue, sanyue, liuyue, bayue, jiuyue, shiyue, shieryue, shiwuyue, shibayue, ershisiyue, sanshiwuyue, sanshiliuyue, sishibayue, liushiyue, jiushiliuyue, zongji, round(zongji/sum_all,2) as zhanbi from TMPddd ) dd GROUP BY STAT_PERIOD ;
DROP TABLE IF EXISTS TMPzzz; CREATE TEMPORARY TABLE TMPzzz AS select STAT_PERIOD, '期限占比总销量', concat(round(shiwutian*100/zongji,2),'%'), concat(round(yiyue*100/zongji,2),'%'), concat(round(eryue*100/zongji,2),'%'), concat(round(sanyue*100/zongji,2),'%'), concat(round(liuyue*100/zongji,2),'%'), concat(round(bayue*100/zongji,2),'%'), concat(round(jiuyue*100/zongji,2),'%'), concat(round(shiyue*100/zongji,2),'%'), concat(round(shieryue*100/zongji,2),'%'), concat(round(shiwuyue*100/zongji,2),'%'), concat(round(shibayue*100/zongji,2),'%'), concat(round(ershisiyue*100/zongji,2),'%'), concat(round(sanshiwuyue*100/zongji,2),'%'), concat(round(sanshiliuyue*100/zongji,2),'%'), concat(round(sishibayue*100/zongji,2),'%'), concat(round(liushiyue*100/zongji,2),'%'), concat(round(jiushiliuyue*100/zongji,2),'%'), concat(round(zongji*100/zongji,2),'%'), '100%', 3 from TMPyyy ;
DROP TABLE IF EXISTS TMPkeai; CREATE TEMPORARY TABLE TMPkeai AS select * from ( select * FROM TMPeee union all select * from tmpyyy union all select * from tmpzzz ) o order BY STAT_PERIOD desc,num ; select STAT_PERIOD, SALES_TYPE, case when shiwutian='0.00' then '' when shiwutian='0.00%' then '' else shiwutian end as shiwutian,
case when yiyue='0.00' then '' when yiyue='0.00%' then '' else yiyue end as yiyue,
case when eryue='0.00' then '' when eryue='0.00%' then '' else eryue end as eryue,
case when sanyue='0.00' then '' when sanyue='0.00%' then '' else sanyue end as sanyue,
case when liuyue='0.00' then '' when liuyue='0.00%' then '' else liuyue end as liuyue,
case when bayue='0.00' then '' when bayue='0.00%' then '' else bayue end as bayue,
case when jiuyue='0.00' then '' when jiuyue='0.00%' then '' else jiuyue end as jiuyue,
case when shiyue='0.00' then '' when shiyue='0.00%' then '' else shiyue end as shiyue,
case when shieryue='0.00' then '' when shieryue='0.00%' then '' else shieryue end as shieryue,
case when shiwuyue='0.00' then '' when shiwuyue='0.00%' then '' else shiwuyue end as shiwuyue,
case when shibayue='0.00' then '' when shibayue='0.00%' then '' else shibayue end as shibayue,
case when ershisiyue='0.00' then '' when ershisiyue='0.00%' then '' else ershisiyue end as ershisiyue,
case when sanshiwuyue='0.00' then '' when sanshiwuyue='0.00%' then '' else sanshiwuyue end as sanshiwuyue,
case when sanshiliuyue='0.00' then '' when sanshiliuyue='0.00%' then '' else sanshiliuyue end as sanshiliuyue,
case when sishibayue='0.00' then '' when sishibayue='0.00%' then '' else sishibayue end as sishibayue,
case when shibayue='0.00' then '' when shibayue='0.00%' then '' else shibayue end as shibayue, case when liushiyue='0.00' then '' when liushiyue='0.00%' then '' else liushiyue end as liushiyue,
case when jiushiliuyue='0.00' then '' when jiushiliuyue='0.00%' then '' else jiushiliuyue end as jiushiliuyue, ZONGJI, ZHANBI from TMPkeai

DROP TABLE IF EXISTS TMPaaa; CREATE TEMPORARY TABLE TMPaaa AS select STAT_PERIOD, sales_type, period, sum(sales_money) as sum from dm_report_dep_sales WHERE 1=1 AND sales_type in ('项目','智投乐','月悦升')
