DROP TABLE IF EXISTS TMPeee; CREATE TEMPORARY TABLE TMPeee AS select STAT_PERIOD, sales_type, shiwutian, yiyue, eryue, sanyue, liuyue, bayue, jiuyue, shiyue, shieryue, shiwuyue, shibayue, ershisiyue, sanshiwuyue, sanshiliuyue, sishibayue, liushiyue, jiushiliuyue, zongji, CONCAT(round(zongji*100/sum_all,2),'%') as zhanbi, num from TMPddd ;
