select STAT_PERIOD, sales_type, sum(sales_money) as zhanbi from dm_report_dep_sales WHERE 1=1
AND STAT_PERIOD BETWEEN #{reg_begindate} AND #{reg_enddate}
AND name='留存情况' GROUP BY STAT_PERIOD DESC,sales_type desc,PERIOD

